void main() {
  runApp(const AgroMart());
}

class AgroMart extends StatelessWidget {
  const AgroMart({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'AgroMart',
      theme: ThemeData(
        colorScheme: ColorScheme.fromSeed(
          seedColor: Colors.green,
          brightness: Brightness.light,
        ),
        useMaterial3: true,
      ),
      darkTheme: ThemeData(
        colorScheme: ColorScheme.fromSeed(
          seedColor: Colors.green,
          brightness: Brightness.dark,
        ),
        useMaterial3: true,
      ),
      themeMode: ThemeMode.system,
      debugShowCheckedModeBanner: false,
      home: const HomePage(),
    );
  }
}

class HomePage extends StatefulWidget {
  const HomePage({super.key});

  @override
  State<HomePage> createState() => _HomePageState();
}

class _HomePageState extends State<HomePage> {
  int _selectedIndex = 0;

  final List<Widget> _pages = [
    const HomeScreen(),
    const DealsScreen(),
    const CartScreen(),
  ];

  void _onItemTapped(int index) {
    setState(() {
      _selectedIndex = index;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('AgroMart'),
        backgroundColor: Colors.green.withOpacity(0.3),actions: [
        // Notification Icon
        IconButton(
          icon: const Icon(Icons.notifications),
          onPressed: () {
            // Handle notification icon tap
          },
        ),

        // Outlined Sign Up Button
        OutlinedButton(
          onPressed: () {
            Navigator.push(
              context,
              MaterialPageRoute(builder: (context) => const SignUpScreen()),
            );
          },
          style: OutlinedButton.styleFrom(
            side: const BorderSide(color: Colors.white), // Border color
            shape: RoundedRectangleBorder(
              borderRadius: BorderRadius.circular(20),
            ),
          ),
          child: const Text(
            'Sign Up',
            style: TextStyle(color: Colors.white),
          ),
        ),
        const SizedBox(width: 10), // Space between the two buttons
      ],
      ),
      //FloatAction button
      floatingActionButton: FloatingActionButton(
        onPressed: () {
          // Navigate to the ChatPage
          Navigator.push(
            context,
            MaterialPageRoute(builder: (context) => ChatPage()),
          );
        },
        elevation: 10,  // This should be outside of the onPressed function
        child: Icon(Icons.messenger),  // This should be outside of the onPressed function
        backgroundColor: Colors.green,  // This should be outside of the onPressed function
      ),


      drawer: Drawer(
        child: Container(
          color: Colors.green.withOpacity(0.3),
          child: ListView(
            padding: EdgeInsets.zero,
            children: <Widget>[
              const DrawerHeader(
                decoration: BoxDecoration(color: Colors.green),

                  child: Text(
                    'Menu',
                    style: TextStyle(color: Colors.white, fontSize: 20),

                ),
              ),
        ListTile(
          leading: const Icon(Icons.shopping_cart), // Icon for Cart
          title: const Text('Cart'),
          onTap: () {
            Navigator.pop(context);
            setState(() {
              _selectedIndex = 2; // Navigate to Cart
            });
          },
        ),
        ListTile(
          leading: const Icon(Icons.settings), // Icon for Settings
          title: const Text('Settings'),
          onTap: () {
            Navigator.pop(context);
            // Handle settings navigation
          },
        ),
        ListTile(
          leading: const Icon(Icons.info), // Icon for About Us
          title: const Text('About Us'),
          onTap: () {
            Navigator.pop(context);
            // Handle about us navigation
          },
        ),
        ListTile(
          leading: const Icon(Icons.contact_mail), // Icon for Contact Us
          title: const Text('Contact Us'),
          onTap: () {
            Navigator.pop(context);
            // Handle contact us navigation
          },
        ),
    ]
      ),

        ),
      ),
      body: _pages[_selectedIndex],
      bottomNavigationBar: BottomNavigationBar(
        items: const <BottomNavigationBarItem>[
          BottomNavigationBarItem(
            icon: Icon(Icons.home),
            label: 'Home',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.local_offer),
            label: 'Deals',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.shopping_cart),
            label: 'Cart',
          ),
        ],
        currentIndex: _selectedIndex,
        onTap: _onItemTapped,
        backgroundColor: Colors.green.withOpacity(0.3),
      ),
    );
  }
}

class HomeScreen extends StatelessWidget {
  const HomeScreen({super.key});

  @override
  Widget build(BuildContext context) {
    final List<Map<String, dynamic>> featuredProducts = [
      {
        'image': 'data:image/jpeg;base64,',
        'name': 'Organic Carrots',
        'price': 2.99,
      },
      {
        'image': 'data:image/jpeg;base64,',
        'name': 'Fresh Apples',
        'price': 3.49,
      },
      {
        'image': 'data:image/jpeg;base64,',
        'name': 'Whole Wheat Flour',
        'price': 4.99,
      },
      {
        'image': 'data:image/jpeg;base64,',
        'name': 'Free-Range Eggs',
        'price': 5.79,
      },
    ];

    return SingleChildScrollView(
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          const SizedBox(height: 16),
          // Hero Section
          Image.network(
            'data:image/jpeg;base64,',
            fit: BoxFit.cover,
            width: double.infinity,
            height: 200,
          ),
          const SizedBox(height: 16),
          // Featured Products
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 16),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                const Text(
                  'Featured Products',
                  style: TextStyle(
                    fontSize: 18,
                    fontWeight: FontWeight.bold,
                  ),
                ),
                const SizedBox(height: 8),
                SizedBox(
                  height: 200,
                  child: ListView.builder(
                    scrollDirection: Axis.horizontal,
                    itemCount: featuredProducts.length,
                    itemBuilder: (context, index) {
                      final product = featuredProducts[index];
                      return Padding(
                        padding: const EdgeInsets.all(8.0),
                        child: SizedBox(
                          width: 150,
                          child: Column(
                            crossAxisAlignment: CrossAxisAlignment.start,
                            children: [
                              ClipRRect(
                                borderRadius: BorderRadius.circular(8),
                                child: Image.network(
                                  product['image'] as String,
                                  fit: BoxFit.cover,
                                  width: 150,
                                  height: 100,
                                ),
                              ),
                              const SizedBox(height: 8),
                              Text(
                                product['name'] as String,
                                style: const TextStyle(
                                  fontWeight: FontWeight.bold,
                                ),
                              ),
                              const SizedBox(height: 4),
                              Text(
                                '\$${(product['price'] as double).toStringAsFixed(2)}',
                                style: const TextStyle(
                                  color: Colors.green,
                                  fontWeight: FontWeight.bold,
                                ),
                              ),
                              const SizedBox(height: 8),
                              ElevatedButton(
                                onPressed: () {
                                  // Add to cart functionality
                                },
                                style: ElevatedButton.styleFrom(
                                  backgroundColor: Colors.green,
                                  foregroundColor: Colors.white,
                                ),
                                child: const Text('Add to Cart'),
                              ),
                            ],
                          ),
                        ),
                      );
                    },
                  ),
                ),
              ],
            ),
          ),
          const SizedBox(height: 16),
          // Categories
          const Padding(
            padding: EdgeInsets.symmetric(horizontal: 16),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  'Categories',
                  style: TextStyle(
                    fontSize: 18,
                    fontWeight: FontWeight.bold,
                  ),
                ),
                SizedBox(height: 8),
                Wrap(
                  spacing: 8,
                  runSpacing: 8,
                  children: [
                    CategoryChip(label: 'Fruits'),
                    CategoryChip(label: 'Vegetables'),
                    CategoryChip(label: 'Grains'),
                    CategoryChip(label: 'Livestock'),
                    CategoryChip(label: 'Seeds'),
                    CategoryChip(label: 'Fertilizers'),
                    CategoryChip(label: 'Tools')
                  ],
                ),
              ],
            ),
          ),
          const SizedBox(height: 16),
          // Promotions
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 16),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                const Text(
                  'Promotions',
                  style: TextStyle(
                    fontSize: 18,
                    fontWeight: FontWeight.bold,
                  ),
                ),
                const SizedBox(height: 8),
                SizedBox(
                  height: 100,
                  child: ListView(
                    scrollDirection: Axis.horizontal,
                    children: const [
                      PromotionCard(
                        image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSGNufDYRfH0Q1yhb5Mm6oQoKzDsZ2yyfIXpCjbY9LiGzHCCTUIWANJdl1SyI6S5vuzdEg&usqp=CAU',
                        title: '20% Off Vegetables',
                      ),
                      PromotionCard(
                        image: 'data:image/png;base64,',
                        title: 'Free Shipping on Orders Over \$50',
                      ),
                      PromotionCard(
                        image: 'data:image/png;base64,',
                        title: 'Buy One, Get One Free on Fruits',
                      ),
                    ],
                  ),
                ),
              ],
            ),
          ),
          const SizedBox(height: 10),
          // Testimonials
          const Padding(
            padding: EdgeInsets.symmetric(horizontal: 10),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  'Testimonials',
                  style: TextStyle(
                    fontSize: 18,
                    fontWeight: FontWeight.bold,
                  ),
                ),
                SizedBox(height: 8),
                TestimonialCard(
                  name: 'John Doe',
                  text: 'I love shopping at AgroMart. The prices are great and the quality is amazing.',
                ),
                SizedBox(height: 8),
                TestimonialCard(
                  name: 'Jane Smith',
                  text: 'AgroMart has become my go-to for all my farming and gardening needs.',
                ),
              ],
            ),
          ),
          const SizedBox(height: 16),
        ],
      ),
    );
  }
}
